solution.base:
function isPalindrome(string) {
  let cleanedString = string.replace(/\s+/g, '').toLowerCase();
  let arr = cleanedString.split('');
  let reversArr = arr.reverse();
  let resString = reversArr.join('');
  let res = cleanedString === resString;
  return res;
}

solution.es6:
const isPalindrome = (str) => {
  const cleanedString = str.replace(/\s+/g, '').toLowerCase();
  return cleanedString === cleanedString.split('').reverse().join('');
};

