function findShort(string) {
  let wordsArr = string.split(' ');
  let shortestWord = wordsArr[0];
  for (let word of wordsArr) {
    if (word.length < shortestWord.length) {
      shortestWord = word;
    }
  }
  return shortestWord;
}

function toInitials(name) {
  const nameArr = name.split(' ');
  const firstLetterArr = nameArr.map(function(el) {
    return el.slice(0, 1).toUpperCase() + '.';
  });
  const initials = firstLetterArr.join('');
  return initials;
}

function sumDigits(number) {
  const moduleNumber = Math.abs(number);
  const str = moduleNumber.toString();
  const arr = str.split('');
  const res = arr.reduce(function(sum, cur) {
    return Number(sum) + Number(cur);
  }, 0);
  return res;
}

function minMax(arr) {
  const res = [];
  let minValue = Math.min.apply(null, arr);
  let maxValue = Math.max.apply(null, arr);
  res.push(minValue, maxValue);
  return res;
}
